mapper.insert:

insert into ClassSchedule (course_id, course, teacher_id, 
  teacher, academy, class_name, 
  specialty, grade, semester
  )
values (#{courseId,jdbcType=VARCHAR}, #{course,jdbcType=VARCHAR}, #{teacherId,jdbcType=VARCHAR}, 
  #{teacher,jdbcType=VARCHAR}, #{academy,jdbcType=VARCHAR}, #{className,jdbcType=VARCHAR}, 
  #{specialty,jdbcType=VARCHAR}, #{grade,jdbcType=VARCHAR}, #{semester,jdbcType=VARCHAR}
  )

mapper.select:
select course_id, course, teacher_id, teacher, academy, class_name, specialty, grade,
       semester
from ClassSchedule where academy=#{academyname} and course=#{course}

select course_id, course, teacher_id, teacher, academy, class_name, specialty, grade,
       semester
from ClassSchedule where teacher_id=#{teacherid}
